import React, { Component } from "react";
import Sidebar from "../Components/Sidebar";
import NavBar from "../Components/NavBar";
import UserContext from "../Context/UserContext";

import http from "../APIServices/httpService";
import config from "../APIServices/config.json";
import { ToastContainer } from "react-toastify";
import { Box, Container, Grid } from "@material-ui/core";

class Inbox extends Component {
  static contextType = UserContext;

  constructor(props) {
    super(props);
    this.state = {
      errors: {},
      team: [],
      inbox: [],
    };
  }

  async componentDidMount() {
    const { history } = this.props;

    if (this.context.currentUser.name === null) {
      history.push("/");
    }

    http
      .get(config.apiEndpoint + "/inbox/" + this.context.currentUser.teamID)
      .then((res) => {
        this.setState({ inbox: res.data });
      });
    http
      .get(config.apiEndpoint + "/team/" + this.context.currentUser.teamID)
      .then((res) => {
        this.setState({ team: res.data });
      });
  }
  render() {
    const { inbox } = this.state;

    return (
      <React.Fragment>
        <ToastContainer />

        <div class="d-flex" id="wrapper">
          <Sidebar activePage="inbox" />
          <div id="page-content-wrapper">
            <NavBar pagename="Inbox" budget={this.context.currentUser.budget} />
            <br />
            <div>
              <center>
                <table class="table">
                  <thead class="thead-light">
                    <tr>
                      <th scope="col">Sender</th>
                      <th scope="col">Message</th>
                      <th scope="col">TimeStamp</th>
                    </tr>
                  </thead>
                  <tbody>
                    {inbox.map((inbox) => (
                      <tr key={inbox.inbox_id}>
                        <td>{inbox.sender}</td>
                        <td>{inbox.message}</td>
                        <td>{("" + inbox.stamp).substring(0, 10)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </center>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Inbox;
