import React from "react";

const Ticket = ({ databasename, name, description, onChange, id, checked }) => {
  return (
    <div class="cardData">
      <h1>Question: </h1>
      <p>{description}</p>

      <label class="form-check-label">
        <input type="checkbox" checked={checked} onChange={onChange} />
        Yes
      </label>
      <label class="form-check-label">
        <input type="checkbox" checked={checked} onChange={onChange} />
        No
      </label>
      <button
        type="button"
        onClick={onClick}
        class="btn ticket rounded box-shadow"
        name={name}
        id={id}
      ></button>
    </div>
  );
};

export default Ticket;
