import React, { Component } from "react";
import Sidebar from "../Components/Sidebar";
import NavBar from "../Components/NavBar";
import DataPack from "../Components/DataPack";

import UserContext from "../Context/UserContext";
import { Box, Container, Grid, Typography } from "@material-ui/core";

class BuyData extends Component {
  static contextType = UserContext;

  state = {
    cost: 0,
    data: [
      //we will read in this for each team.  have one table for datapacks, one for unique links? or one table with both
      {
        id: 1,
        name: "Test data pack using state",
        value: 500,
        description: "state test read this in from database",
      },
      {
        id: 2,
        name: "test 2 data pack ",
        value: 700,
        description: "state test 2",
      },
    ],
  };

  handleClick = (e) => {
    //this.state.cost = e.target.value;
    console.log(this.state.cost);
  };

  render() {
    return (
      <React.Fragment>
        <Box display="flex" id="wrapper">
          <Sidebar activePage="buydata" />
          <Container id="page-content-wrapper">
            <NavBar
              pagename="Buy Data"
              budget={this.context.currentUser.budget}
            />
            <Box
              px={5}
              py={4}
              className="bg-white box-shadow rounded"
              minHeight={"calc(100vh - 140px)"}
            >
              <br />
              <div class="row">
                <div class="column">
                  {this.state.data.map((data) => (
                    <tr key={data.id}>
                      <DataPack
                        name={data.name}
                        value={data.value}
                        description={data.description}
                        onClick={this.handleClick}
                      ></DataPack>
                      <br></br>
                    </tr>
                  ))}
                </div>
              </div>
            </Box>
          </Container>
        </Box>
      </React.Fragment>
    );
  }
}

export default BuyData;
