import React, { Component } from "react";
import Sidebar from "../Components/Sidebar";
import NavBar from "../Components/NavBar";
import MarketingPack from "../Components/MarketingPack";
import UserContext from "../Context/UserContext";

class Marketing extends Component {
  static contextType = UserContext;

  state = {
    facebook: false,
    twitter: false,
    instagram: false,
    television: false,
  };

  //on mount, read in state values

  onClick = (e) => {
    if (e.currentTarget.name === "Twitter") {
      this.setState({ twitter: true });
    }
    if (e.currentTarget.name === "Instagram") {
      this.setState({ instagram: true });
    }
    if (e.currentTarget.name === "Facebook") {
      this.setState({ facebook: true });
    }
    if (e.currentTarget.name === "Television") {
      this.setState({ television: true });
    }
  };
  //make api calls here, adjust budget

  render() {
    const { facebook, instagram, twitter, television } = this.state;
    return (
      <React.Fragment>
        <div class="d-flex" id="wrapper">
          <Sidebar />
          <div id="page-content-wrapper">
            <NavBar
              pagename="Marketing"
              budget={this.context.currentUser.budget}
            />
            <br />
            <div class="row">
              <div class="column">
                {facebook === true && (
                  <div>
                    <MarketingPack
                      name="Facebook"
                      value="5000"
                      onClick={this.onClick}
                      purchased="true"
                    />
                  </div>
                )}
                {facebook === false && (
                  <div>
                    <MarketingPack
                      name="Facebook"
                      value="5000"
                      onClick={this.onClick}
                      displayButton="true"
                    />
                  </div>
                )}
                <br />
                {twitter === true && (
                  <div>
                    <MarketingPack
                      name="Twitter"
                      value="5000"
                      onClick={this.onClick}
                      purchased="true"
                    />
                  </div>
                )}
                {twitter === false && (
                  <div>
                    <MarketingPack
                      name="Twitter"
                      value="5000"
                      onClick={this.onClick}
                      displayButton="true"
                    />
                  </div>
                )}
              </div>
              <div class="column">
                {instagram === true && (
                  <div>
                    <MarketingPack
                      name="Instagram"
                      value="5000"
                      onClick={this.onClick}
                      purchased="true"
                    />
                  </div>
                )}
                {instagram === false && (
                  <div>
                    <MarketingPack
                      name="Instagram"
                      value="5000"
                      onClick={this.onClick}
                      displayButton="true"
                    />
                  </div>
                )}
                <br />
                {television === true && (
                  <div>
                    <MarketingPack
                      name="Television"
                      value="5000"
                      onClick={this.onClick}
                      purchased="true"
                    />
                  </div>
                )}
                {television === false && (
                  <div>
                    <MarketingPack
                      name="Television"
                      value="5000"
                      onClick={this.onClick}
                      displayButton="true"
                    />
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Marketing;
