import React, { Component } from "react";
import Sidebar from "../Components/Sidebar";
import NavBar from "../Components/NavBar";
import DataPack from "../Components/DataPack";

import UserContext from "../Context/UserContext";

class BuyData extends Component {
  static contextType = UserContext;

  state = {
    cost: 0,
    data: [
      //we will read in this for each team.  have one table for datapacks, one for unique links? or one table with both
      {
        id: 1,
        name: "Test data pack using state",
        value: 500,
        description: "state test read this in from database",
      },
      {
        id: 2,
        name: "test 2 data pack ",
        value: 700,
        description: "state test 2",
      },
    ],
  };

  handleClick = (e) => {
    //this.state.cost = e.target.value;
    console.log(this.state.cost);
  };

  render() {
    return (
      <React.Fragment>
        <div class="d-flex" id="wrapper">
          <Sidebar />
          <div id="page-content-wrapper">
            <NavBar
              pagename="Buy Data"
              budget={this.context.currentUser.budget}
            />
            <br />
            <div class="row">
              <div class="column">
                {this.state.data.map((data) => (
                  <tr key={data.id}>
                    <DataPack
                      name={data.name}
                      value={data.value}
                      description={data.description}
                      onClick={this.handleClick}
                    ></DataPack>
                    <br></br>
                  </tr>
                ))}
              </div>
              <div class="column">
                <div class="cardData">
                  <h5>Competitor Data One: $10000</h5>
                  <p>
                    Graphs displaying the amount of inventory purchased by each
                    team, data displaying the comparison of latitude and
                    longitude per each location
                  </p>
                  <button
                    type="button"
                    // onClick={this.handleClick}
                    class="btn btn-primary"
                    value="10000"
                    name="data10"
                  >
                    Purchase
                  </button>
                </div>
                <br></br>

                <div class="cardData">
                  <h5>Customer Age Data: $4000</h5>
                  <p>
                    We collected age information of 2000 customers close to your
                    store.
                  </p>
                  <button
                    type="button"
                    //onClick={this.handleClick}
                    class="btn btn-primary"
                    value="4000"
                    name="data7"
                  >
                    Purchase
                  </button>
                </div>
              </div>
              <div class="column">
                <div class="cardData">
                  <h5>Basic Population Survey: $3000</h5>
                  <p>
                    This survey is a rough estimate of the population, or number
                    of potential customers, in the area.
                  </p>
                  <button
                    type="button"
                    //onClick={this.handleClick}
                    class="btn btn-primary"
                    value="3000"
                    name="basicPop"
                  >
                    Purchase
                  </button>
                </div>
                <br></br>
                <div class="cardData">
                  <h5>Detailed Population Survey: $7000</h5>
                  <p>
                    This survey is a more specific estimate of the population,
                    or number of potential customers, in the area.
                  </p>

                  <button
                    type="button"
                    // onClick={this.handleClick}
                    class="btn btn-primary"
                    value="7000"
                    name="detailedPop"
                  >
                    Purchase
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default BuyData;
