import React, { Component } from "react";
import Sidebar from "../Components/Sidebar";
import NavBar from "../Components/NavBar";
import Ticket from "../Components/Ticket";
import UserContext from "../Context/UserContext";

import http from "../APIServices/httpService";
import config from "../APIServices/config.json";
import { toast, ToastContainer } from "react-toastify";
import { Box, Container, Grid, Typography } from "@material-ui/core";

class Tickets extends Component {
  static contextType = UserContext;

  state = {
    option1: false, //read these values in
    option2: false,

    option3: false,
    option4: false,

    selected: false,

    option1Name: "option 1 name",
    option1Description: "option 1 description",

    option2Name: "option 2 name",
    option2Description: "option 2 description",

    option3Name: "option 3 name",
    option3Description: "option 3 description",

    option4Name: "option 4 name",
    option4Description: "option 4 description",

    teamTickets: [],
    ticketOptions: [],
    ticketBundles: [],
    ticketDiscounts: [],

    test: false,
  };

  async componentDidMount() {
    console.log("page started");
    http
      .get(config.apiEndpoint + "/tickets/" + this.context.currentUser.teamID)
      .then((res) => {
        console.log(res.data);
        this.setState({ teamTickets: res.data });
      });
    http.get(config.apiEndpoint + "/ticketoptions/").then((res) => {
      console.log(res.data);
      this.setState({ ticketOptions: res.data });
    });
    http.get(config.apiEndpoint + "/ticketbundle/").then((res) => {
      console.log(res.data);
      this.setState({ ticketBundles: res.data });
    });
    http.get(config.apiEndpoint + "/ticketdiscounts/").then((res) => {
      console.log(res.data);
      this.setState({ ticketDiscounts: res.data });
    });
  }

  changeColor() {
    //used to c
    this.setState({ selected: !this.state.selected });
  }

  handleClick = (e) => {
    let type = e.currentTarget.attributes.databasename.value;
    let displayName = e.currentTarget.name;
    let id = e.currentTarget.id;
    this.state.teamTickets[type] = id;
    toast.success(displayName + " Option " + id + " Selected");
    http.put(
      config.apiEndpoint + "/tickets/" + this.context.currentUser.teamID,
      this.state.teamTickets
    );
    this.setState({ test: true });
  };

  render() {
    let btn_class = (this.state.selected = "selected");
    const { teamTickets } = this.state;

    return (
      <React.Fragment>
        <ToastContainer />

        <div class="d-flex" id="wrapper">
          <Sidebar activePage="tickets" />
          <Container id="page-content-wrapper">
            <NavBar budget={this.context.currentUser.budget} />
            <Box
              px={5}
              py={4}
              className="bg-white box-shadow rounded"
              minHeight={"calc(100vh - 140px)"}
            >
              <Grid container spacing={3}>
                <Grid item xs={12}>
                  <Box
                    p={1}
                    textAlign="center"
                    fontWeight="fontWeightBold"
                    className="bg-blue box-shadow rounded"
                  >
                    Ticket Choices
                  </Box>
                </Grid>
                Grid item xs={12}>
                <Box p={2} className="bg-lightblue box-shadow rounded">
                  <p>
                    Lorem ipsum dolor sit amet, at illud delicata definitionem
                    nec. Eu vix omnium animal, ius quas fierent ad, vix omnes
                    theophrastus ea. Nobis corpora cu sea, ne accusam officiis
                    eum. Ne vel euismod nostrum. Latine tamquam suavitate nec
                    at.Ea idque apeirian eos, vim dicat moderatius te. Vel natum
                    solum corpora ea. Viderer tacimates qualisque et est, no ius
                    percipit splendide mediocritatem, laudem bonorum accusam ut
                    per. In oportere reformidans quo, et ius suscipit erroribus,
                    novum tractatos ei vix. Quo wisi veniam id. Saepe salutandi
                    voluptaria ad cum.{" "}
                  </p>
                  <p>
                    Nostrum quaestio splendide id sit. Ex enim solet vix. Per ei
                    gloriatur democritum, stet iriure expetendis at vel, in usu
                    mazim viris libris. Doctus cetero fabellas qui ex, vim nisl
                    fuisset ne, sea eu laoreet corrumpit necessitatibus. Sit et
                    falli accusata similique, eu iudico delenit vim. Laudem
                    volutpat prodesset ei vim, eripuit facilisis dignissim nam
                    ei, sit ea graece doctus gloriatur. Vis brute saperet eu,
                    errem recteque suscipiantur vis ea, ei vix dicit luptatum
                    molestiae.{" "}
                  </p>
                  <p>
                    Et vidit accusamus contentiones pri, his tale facer et. Hinc
                    facer commodo ex sed. Movet scaevola conceptam id qui, facer
                    alterum cum id, lucilius eloquentiam ea his. Sea falli
                    tantas ea, ea pri mentitum sententiae, te mel congue
                    rationibus. Ea quod regione eum, cum ei eius soluta oblique,
                    nam no omnis recusabo.
                  </p>
                </Box>
              </Grid>
            </Box>
          </Container>

          <div id="page-content-wrapper">
            <NavBar
              pagename="Tickets"
              budget={this.context.currentUser.budget}
            />
            <br />
            <nav class="center">
              Currently Selected Options: Adult and Child: Option{" "}
              {teamTickets.adult_children}, Bundle Option: {teamTickets.bundle},
              Discount Option: {teamTickets.discount}
            </nav>
            <div class="row">
              <div class="column">
                <h1>Adult and Child</h1>
                {this.state.ticketOptions.map((ticketOptions) => (
                  <tr key={ticketOptions.id}>
                    <Ticket
                      name="Adult and Child"
                      databasename="adult_children"
                      classname={btn_class}
                      description={ticketOptions.description}
                      onClick={(e) => {
                        this.handleClick(e);
                      }}
                      id={ticketOptions.option_id}
                      // selected={this.state.option[ticketOptions.id]}
                    ></Ticket>
                    <br></br>
                  </tr>
                ))}
              </div>
              <div class="column">
                <h1>Bundles</h1>
                {this.state.ticketBundles.map((ticketBundles) => (
                  <tr key={ticketBundles.id}>
                    <Ticket
                      name="Bundle"
                      databasename="bundle"
                      classname={btn_class}
                      description={ticketBundles.description}
                      onClick={(e) => {
                        this.handleClick(e);
                      }}
                      id={ticketBundles.bundle_id}
                      // selected={this.state.option[ticketOptions.id]}
                    ></Ticket>
                    <br></br>
                  </tr>
                ))}
              </div>
              <div class="column">
                <h1>Discounts</h1>
                {this.state.ticketDiscounts.map((ticketDiscounts) => (
                  <tr key={ticketDiscounts.id}>
                    <Ticket
                      name="Discount"
                      databasename="discount"
                      classname={btn_class}
                      description={ticketDiscounts.description}
                      onClick={(e) => {
                        this.handleClick(e);
                      }}
                      id={ticketDiscounts.discount_id}
                      // selected={this.state.option[ticketOptions.id]}
                    ></Ticket>
                    <br></br>
                  </tr>
                ))}
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Tickets;
