import React, { Component } from "react";
import Sidebar from "../Components/Sidebar";
import NavBar from "../Components/NavBar";
import MarketingPack from "../Components/MarketingPack";
import UserContext from "../Context/UserContext";

import http from "../APIServices/httpService";
import config from "../APIServices/config.json";
import { toast, ToastContainer } from "react-toastify";
import { Box, Container, Grid } from "@material-ui/core";

class Marketing extends Component {
  static contextType = UserContext;

  state = {
    facebook: false,
    twitter: false,
    instagram: false,
    television: false,
  };

  async componentDidMount() {
    const { history } = this.props;

    if (this.context.currentUser.name === null) {
      history.push("/");
    }

    http
      .get(config.apiEndpoint + "/inbox/" + this.context.currentUser.teamID)
      .then((res) => {
        this.setState({ inbox: res.data });
      });
    http
      .get(config.apiEndpoint + "/team/" + this.context.currentUser.teamID)
      .then((res) => {
        this.setState({ team: res.data });
      });
  }

  onClick = (e) => {
    if (e.currentTarget.name === "Twitter") {
      this.setState({ twitter: true });
    }
    if (e.currentTarget.name === "Instagram") {
      this.setState({ instagram: true });
    }
    if (e.currentTarget.name === "Facebook") {
      this.setState({ facebook: true });
    }
    if (e.currentTarget.name === "Television") {
      this.setState({ television: true });
    }
  };
  //make api calls here, adjust budget

  render() {
    const { facebook, instagram, twitter, television } = this.state;
    return (
      <React.Fragment>
        <ToastContainer />

        <Box display="flex" id="wrapper">
          <Sidebar activePage="marketing" />
          <Container id="page-content-wrapper">
            <NavBar
              pagename="Marketing"
              budget={this.context.currentUser.budget}
            />
            <Box
              px={5}
              py={4}
              className="bg-white box-shadow rounded"
              minHeight={"calc(100vh - 140px)"}
            >
              <Grid container>
                <Grid item xs={12}>
                  <Box
                    p={1}
                    textAlign="center"
                    fontWeight="fontWeightBold"
                    className="bg-blue box-shadow rounded"
                  >
                    Marketing
                  </Box>
                </Grid>
              </Grid>
              <br />
              <Box class="columns2">
                <div class="column">
                  {facebook === true && (
                    <div>
                      <MarketingPack
                        name="Facebook"
                        value="5000"
                        onClick={this.onClick}
                        purchased="true"
                      />
                    </div>
                  )}
                  {facebook === false && (
                    <div>
                      <MarketingPack
                        name="Facebook"
                        value="5000"
                        onClick={this.onClick}
                        displayButton="true"
                      />
                    </div>
                  )}
                  <br />
                  {twitter === true && (
                    <div>
                      <MarketingPack
                        name="Twitter"
                        value="5000"
                        onClick={this.onClick}
                        purchased="true"
                      />
                    </div>
                  )}
                  {twitter === false && (
                    <div>
                      <MarketingPack
                        name="Twitter"
                        value="5000"
                        onClick={this.onClick}
                        displayButton="true"
                      />
                    </div>
                  )}
                </div>
                <div class="column">
                  {instagram === true && (
                    <div>
                      <MarketingPack
                        name="Instagram"
                        value="5000"
                        onClick={this.onClick}
                        purchased="true"
                      />
                    </div>
                  )}
                  {instagram === false && (
                    <div>
                      <MarketingPack
                        name="Instagram"
                        value="5000"
                        onClick={this.onClick}
                        displayButton="true"
                      />
                    </div>
                  )}
                  <br />
                  {television === true && (
                    <div>
                      <MarketingPack
                        name="Television"
                        value="5000"
                        onClick={this.onClick}
                        purchased="true"
                      />
                    </div>
                  )}
                  {television === false && (
                    <div>
                      <MarketingPack
                        name="Television"
                        value="5000"
                        onClick={this.onClick}
                        displayButton="true"
                      />
                    </div>
                  )}
                </div>
              </Box>
              <div></div>
            </Box>
            <br />
          </Container>
        </Box>
      </React.Fragment>
    );
  }
}

export default Marketing;
